import React, { useEffect, useState } from "react";
import { fetchTasks, createTask, updateTask, deleteTask } from "../api/taskApi";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import { toast } from "react-toastify";

export const HomePage = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchAllTasks = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      toast.error("Vous devez être connecté pour voir vos tâches.");
      return;
    }
    setLoading(true);
    try {
      const data = await fetchTasks(token);
      setTasks(data);
    } catch (error) {
      console.error("Erreur lors du chargement des tâches :", error);
      toast.error("Une erreur est survenue lors du chargement des tâches.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllTasks();
  }, []);

  const handleTaskCreated = async (task) => {
    const token = localStorage.getItem("token");
    try {
      const newTask = await createTask(task, token);
      setTasks((prevTasks) => [...prevTasks, newTask]);
      toast.success("Tâche créée avec succès !");
    } catch (error) {
      console.error("Erreur lors de la création de la tâche :", error);
      toast.error("Impossible de créer la tâche.");
    }
  };

  const handleUpdateTask = async (task) => {
    const token = localStorage.getItem("token");
    try {
      const updatedTask = await updateTask(task._id, task, token);
      setTasks((prevTasks) =>
        prevTasks.map((t) => (t._id === updatedTask._id ? updatedTask : t))
      );
      toast.success("Tâche mise à jour avec succès !");
    } catch (error) {
      console.error("Erreur lors de la mise à jour :", error);
      toast.error("Impossible de mettre à jour la tâche.");
    }
  };

  const handleDeleteTask = async (taskId) => {
    const token = localStorage.getItem("token");
    try {
      await deleteTask(taskId, token);
      setTasks((prevTasks) => prevTasks.filter((t) => t._id !== taskId));
      toast.success("Tâche supprimée avec succès !");
    } catch (error) {
      console.error("Erreur lors de la suppression :", error);
      toast.error("Impossible de supprimer la tâche.");
    }
  };

  return (
    <div>
      <h1>Mes tâches</h1>
      {loading ? (
        <p>Chargement des tâches...</p>
      ) : (
        <>
          <TaskForm onTaskCreated={handleTaskCreated} />
          <TaskList
            tasks={tasks}
            onUpdateTask={handleUpdateTask}
            onDeleteTask={handleDeleteTask}
          />
        </>
      )}
    </div>
  );
};

export default HomePage;
